import React, { useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import {
  CarouselControl,
  Carousel,
  CarouselItem,
  CarouselIndicators,
} from "reactstrap";

const Slides = () => {
  // State for Active index
  const [activeI, setActiveI] = useState(0);

  // State for Animation
  const [animating, setAnimating] = useState(false);

  // Sample items for Carousel
  const items = [
    {
      caption: "Sample Caption One",
      src: "https://media.geeksforgeeks.org/wp-content/uploads/20210425122739/2-300x115.png",
      altText: "Slide One",
    },
    {
      caption: "Sample Caption Two",
      src: "https://media.geeksforgeeks.org/wp-content/uploads/20210425122716/1-300x115.png",
      altText: "Slide Two",
    },
  ];

  // Items array length
  const itemLength = items.length - 1;

  // Previous button for Carousel
  const previousButton = () => {
    if (animating) return;
    const nextIndex = activeI === 0 ? itemLength : activeI - 1;
    setActiveI(nextIndex);
  };

  // Next button for Carousel
  const nextButton = () => {
    if (animating) return;
    const nextIndex = activeI === itemLength ? 0 : activeI + 1;
    setActiveI(nextIndex);
  };

  // Carousel Item Data
  const carouselItemData = items.map((item) => {
    return (
      <CarouselItem
        key={item.src}
        onExited={() => setAnimating(false)}
        onExiting={() => setAnimating(true)}
      >
        <img src={item.src} alt={item.altText} />
      </CarouselItem>
    );
  });

  return (
    <div
      style={{
        display: "block",
        width: 320,
        padding: 30,
      }}
    >
      <h8>ReactJS Reactstrap Carousel Component</h8>
      <Carousel
        previous={previousButton}
        next={nextButton}
        activeI={activeI}
      >
        <CarouselIndicators
          items={items}
          activeI={activeI}
          onClickHandler={(newIndex) => {
            if (animating) return;
            setActiveI(newIndex);
          }}
        />
        {carouselItemData}
        <CarouselControl
          directionText="Prev"
          direction="prev"
          onClickHandler={previousButton}
        />
        <CarouselControl
          directionText="Next"
          direction="next"
          onClickHandler={nextButton}
        />
      </Carousel>
    </div>
  );
};

export default Slides;
